function [theta,itr] = newton(X,Y)    m = length(X);  theta = zeros(3,1);  thetax = ones(3,1);  itr = 0; %to get the iterations required to converge the likelihood function    while (thetax -theta)'*(thetax-theta) > 0.00000001    htheta = 1./(1+e.^(X*theta)); %hypothesis equation     delta = []; %hessian matrix        for i = 1:3      delta(i,:) = ((diag(X(:,i))*X)'*(diag(htheta)*(1-htheta)))';  %value of each column of hessian matrix    end        thetax = theta;        theta = theta - inv(delta)*((Y - htheta)'*X)'; %implementing the convergence    itr += 1;  endend